<?php

namespace App\Controller\Back;

use DateTimeImmutable;
use App\Service\MySlugger;
use App\Entity\CategoryMove;
use App\Form\CategoryMoveType;
use App\Repository\CategoryMoveRepository;
use Symfony\Component\HttpFoundation\Request;
use Symfony\Component\HttpFoundation\Response;
use Symfony\Component\Routing\Annotation\Route;
use Symfony\Component\HttpFoundation\File\UploadedFile;
use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;

/**
 * @Route("/back/category/move")
 */
class CategoryMoveController extends AbstractController
{
    /**
     * @Route("/", name="app_back_category_move_index", methods={"GET"})
     */
    public function index(CategoryMoveRepository $categoryMoveRepository): Response
    {
        return $this->render('back/category_move/index.html.twig', [
            'category_moves' => $categoryMoveRepository->findAll(),
        ]);
    }

    /**
     * @Route("/new", name="app_back_category_move_new", methods={"GET", "POST"})
     */
    public function new(Request $request, CategoryMoveRepository $categoryMoveRepository, MySlugger $slugger): Response
    {
        $categoryMove = new CategoryMove();
        $form = $this->createForm(CategoryMoveType::class, $categoryMove);
        $form->handleRequest($request);

        if ($form->isSubmitted() && $form->isValid()) {
            /** @var UploadedFile $imageFile */
            if ($form->get('image')->getData()) {
                $imageFile = $form->get('image')->getData();
                $format = $imageFile->getMimeType();
                $img = file_get_contents($imageFile);
                $dataBase64 = base64_encode($img);
                $categoryMove->setImage('data:'. $format . ';base64,' . $dataBase64);
            }
            $imageDefault = "data:image/png;base64,";
            if (!$categoryMove->getImage()) {
                $categoryMove->setImage($imageDefault);
            }
            $categoryMove->setSlug($slugger->slugify($categoryMove->getName()));
            $categoryMoveRepository->add($categoryMove, true);

            return $this->redirectToRoute('app_back_category_move_index', [], Response::HTTP_SEE_OTHER);
        }

        return $this->renderForm('back/category_move/new.html.twig', [
            'category_move' => $categoryMove,
            'form' => $form,
        ]);
    }

    /**
     * @Route("/{id}/edit", name="app_back_category_move_edit", methods={"GET", "POST"})
     */
    public function edit(Request $request, CategoryMove $categoryMove, CategoryMoveRepository $categoryMoveRepository): Response
    {
        $form = $this->createForm(CategoryMoveType::class, $categoryMove);
        $form->handleRequest($request);

        if ($form->isSubmitted() && $form->isValid()) {
            /** @var UploadedFile $imageFile */
            if ($form->get('image')->getData()) {
                $imageFile = $form->get('image')->getData();
                $format = $imageFile->getMimeType();
                $img = file_get_contents($imageFile);
                $dataBase64 = base64_encode($img);
                $categoryMove->setImage('data:'. $format . ';base64,' . $dataBase64);
            }
            $categoryMove->setUpdatedAt(new DateTimeImmutable());
            $categoryMoveRepository->add($categoryMove, true);

            return $this->redirectToRoute('app_back_category_move_index', [], Response::HTTP_SEE_OTHER);
        }

        return $this->renderForm('back/category_move/edit.html.twig', [
            'category_move' => $categoryMove,
            'form' => $form,
        ]);
    }
}
